<?phpinclude "header.php";$present_year = date('Y');$mClickCount = array();$friendlyIps = getFriendlyIP();$sql = "SELECT MONTH(vi.datetime) as month,count(vi.id) AS clicks FROM visitors_info as vi WHERE vi.geo_info_status = 1 AND (YEAR(vi.datetime) = YEAR(CURDATE())) AND vi.ip_address NOT IN (" . $friendlyIps . ") GROUP BY MONTH(vi.datetime)";try {	$stmt = $dbcon->prepare($sql, array(PDO::ATTR_CURSOR => PDO::CURSOR_SCROLL));	$stmt->execute();		if($stmt->rowCount() > 0){		$analyticsData = $stmt->fetchALL(PDO::FETCH_ASSOC);		foreach($analyticsData as $aData){			$mClickCount[$aData['month']] = $aData['clicks'];		}			}}catch (PDOException $e){	print $e->getMessage();}$months_names = array(1=>"Jan",2=>"Feb",3=>"Mar",4=>"Apr",5=>"May",6=>"Jun",7=>"Jul",8=>"Aug",9=>"Sep",10=>"Oct",11=>"Nov",12=>"Dec");?><!-- Container --><div id="container">	<div class="shell">				<!-- Small Nav -->		<div class="small-nav">			<a href="#">Home</a>			<span>&gt;</span>              Month wise visitors count		</div>		<!-- End Small Nav -->               <br />		<!-- Main -->		<div id="main">			<div class="cl">&nbsp;</div>															<!-- Table -->					<div class="table">															<!-- Box Head -->					<div class="box-head" style="width:770px;">						<h2 class="left">Monthly Reports for this year <?php echo $present_year; ?></h2>					<!--	<div class="right">							<label>search articles</label>							<input type="text" class="field small-field" />							<input type="submit" class="button" value="search" />						</div> -->					</div>					<!-- End Box Head -->						<table width="80%" border="0" cellspacing="0" cellpadding="0"><?php//echo "<table  class='gridtable'>";echo "<thead><tr><th>Month</th><th>Clicks</th></tr></thead><tbody>";foreach($months_names as $key=>$value){     $cCount = (@$mClickCount[$key] > 0) ? $mClickCount[$key] : 0;  echo "<tr><td>".$value."</td><td>".$cCount."</td></tr>"; }echo "</tbody></table>";?>   </div><!-- Table --><div class="cl">&nbsp;</div>					</div>		<!-- Main -->	</div></div><!-- End Container -->			<?phpinclude "footer.php";?>